function calculateLogBins(start, end, nBins) {
  const logStart = Math.log10(start);
  const logEnd = Math.log10(end);
  const bins = Array.from({length: nBins + 1}, (_, i) =&gt; 
    Math.pow(10, logStart + (logEnd - logStart) * i / nBins)
  );
  return bins;
}







